import { Component } from '@angular/core';
import {FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  serchForm = new FormGroup({
    'movieName': new FormControl(null)
  });

  submitForm(){
    console.log(this.searchForm.value, 'searchForm#');
  }

}
<div class="container mt-5 pt-5">
  <form [formGroup]="searchForm" (ngSubmit)="submitForm()">
    <div class="input-group mb-3">
      <input type="text" class="form-control" class="formControl" formControlName="Pesquisar Filme..." placeholder="Pesquisar Filme...">
      <button class="btn btn-danger" type="button">Pesquisar</button>
    </div>
  </form>
</div>
