import { Component } from '@angular/core';
import { MovieApiService } from 'src/app/services/movie-api.service'; @Component({
    selector: 'app-home',
    templateUrl: './home.component.html',
    styleUrls: ['./home.component.css']
})

 export class HomeComponent {

    constructor(private service:MovieApiService) { }

    bannerResult: any = [];
    trendingMovieResult: any =[];
    actionMovieResult: any=[];
    adventureMovieResult: any=[];
    animationMovieResult: any=[];
    comedyMovieResult: any=[];
    documentaryMovieResult: any=[];
    scienceFictionMovieResult: any=[];
    thrillerMovieResult: any=[];

    ngOnInit(): void {
      this.bannerData();
      this.trendingData();
      this.actionMovieData();
      this.adventureMovieData();
      this.animationMovieData();
      this.comedyMovieData();
      this.documentaryMovieData();
      this.scienceFictionMovieData();
      this.thrillerMovieData();
   }

  // Banner Data
  bannerData(){
    this.service.bannerApiData().subscribe((result)=>{
      console.log(result, 'bannerResult#');
       this.bannerResult = result.results;
    });
  }

  // Trending Data
  trendingData() {
    this.service.trendingMovieApiData().subscribe((result) => {
      this.trendingMovieResult = result.results;
    });
  }

   // Action Movies
  actionMovieData(){
    this.service.fetchActionMovies().subscribe((result)=>{
      this.actionMovieResult = result.results;
    });
  }

  // Adventure Movies
  adventureMovieData(){
    this.service.fetchAdventureMovies().subscribe((result)=>{
      this.adventureMovieResult = result.results;
    });
  }

  // Animation Movies
  animationMovieData(){
    this.service.fetchAnimationMovies().subscribe((result)=>{
      this.animationMovieResult = result.results;
    });
  }

  // Comedy Movies
  comedyMovieData(){
    this.service.fetchComedyMovies().subscribe((result)=>{
      this.comedyMovieResult = result.results;
    });
  }

  // Documentary Movies
  documentaryMovieData(){
    this.service.fetchDocumentaryMovies().subscribe((result)=>{
      this.documentaryMovieResult = result.results;
    });
  }

  // Science-Fiction Movies
  scienceFictionMovieData(){
    this.service.fetchScienceFictionMovies().subscribe((result)=>{
      this.scienceFictionMovieResult = result.results;
    });
  }

  // Thriller Movies
  thrillerMovieData(){
    this.service.fetchThrillerMovies().subscribe((result)=>{
      this.thrillerMovieResult = result.results;
    });
  }
 }
<div class="container-fluid">

    <!-- banner start -->
    <div class="container-fluid p-0">
        <div id="carouselExampleCaptions" class="carousel slide" data-bs-ride="carousel">
            <div class="carousel-inner">
                <div class="carousel-item" data-bs-interval="10000" *ngFor="let b of bannerResult; let i=index" [ngClass]="{active:i===0}">
                    <img src="https://image.tmdb.org/t/p/original/{{b.backdrop_path}}" class="d-block w-100" alt="{{b.title??b.name}}">
                       <div class="carousel-caption d-none d-md-block">
                            <h2>{{b.title??b.name}}</h2>
                            <p>{{b.overview}}</p>
                        </div>
                </div>
            </div>
            <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="prev">
                <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                <span class="visually-hidden">Anterior</span>
            </button>
            <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="next">
                <span class="carousel-control-next-icon" aria-hidden="true"></span>
                <span class="visually-hidden">Próximo</span>
            </button>
        </div>
    </div>
    <!-- banner end -->

    <div class="container my-5">
       <!-- trending movies -->
      <div class="row">
        <h5 class="text-white">Filmes em Destaque</h5>
          <div class="rowposter mt-3 p-2">
            <ng-container *ngFor="let t of trendingMovieResult">
              <img src="https://image.tmdb.org/t/p/original/{{t.poster_path}}" class="rowimg largeposter" alt="t.title" [routerLink]="['/movie',t.id]">
            </ng-container>
          </div>
      </div>

      <!-- action movies -->
    <div class="row mt-5">
  <h5 class="text-white">Filmes de Ação</h5>


    </div>



  </div>
